mapper.insert:
INSERT INTO crm_role_menu(
role_id,
menu_id,
create_time
)VALUES

    (
    #{roleMenu.roleId},
    #{roleMenu.menuId},
    #{roleMenu.createTime}
    )

mapper.select:
SELECT m.menu_id AS menuId, parent_id AS parentId, menu_name AS menuName, permission, href, icon FROM crm_menu m LEFT JOIN crm_role_menu r ON r.menu_id = m.menu_id WHERE menu_type = #{menuType} AND r.role_id IN
#{roleId}
ORDER BY sort ASC
